/**
 * proof request json
 *     {
 *         "name": string,
 *         "version": string,
 *         "nonce": string,
 *         "requested_attributes": { // set of requested attributes
 *              "<attr_referent>": <attr_info>, // see below
 *              ...,
 *         },
 *         "requested_predicates": { // set of requested predicates
 *              "<predicate_referent>": <predicate_info>, // see below
 *              ...,
 *          },
 *         "non_revoked": Optional<<non_revoc_interval>>, // see below,
 *                        // If specified prover must proof non-revocation
 *                        // for date in this interval for each attribute
 *                        // (can be overridden on attribute level)
 *     }
 *
 *     where
 *
 *
 *     attr_referent: Describes requested attribute
 *     {
 *         "name": string, // attribute name, (case insensitive and ignore spaces)
 *         "restrictions": Optional<[<attr_filter>]> // see below,
 *                          // if specified, credential must satisfy to one of the given restriction.
 *         "non_revoked": Optional<<non_revoc_interval>>, // see below,
 *                        // If specified prover must proof non-revocation
 *                        // for date in this interval this attribute
 *                        // (overrides proof level interval)
 *     }
 *     predicate_referent: Describes requested attribute predicate
 *     {
 *         "name": attribute name, (case insensitive and ignore spaces)
 *         "p_type": predicate type (Currently >= only)
 *         "p_value": predicate value
 *         "restrictions": Optional<[<attr_filter>]> // see below,
 *                         // if specified, credential must satisfy to one of the given restriction.
 *         "non_revoked": Optional<<non_revoc_interval>>, // see below,
 *                        // If specified prover must proof non-revocation
 *                        // for date in this interval this attribute
 *                        // (overrides proof level interval)
 *     }
 *     non_revoc_interval: Defines non-revocation interval
 *     {
 *         "from": Optional<int>, // timestamp of interval beginning
 *         "to": Optional<int>, // timestamp of interval ending
 *     }
 *     filter:
 *     {
 *         "schema_id": string, (Optional)
 *         "schema_issuer_did": string, (Optional)
 *         "schema_name": string, (Optional)
 *         "schema_version": string, (Optional)
 *         "issuer_did": string, (Optional)
 *         "cred_def_id": string, (Optional)
 *     }
 * */
@CordaSerializable
data class ProofRequest(
        val version: String,
        val name: String,
        val nonce: String,
        val requestedAttributes: Map<String, ClaimFieldReference>,
        val requestedPredicates: Map<String, ClaimPredicateReference>
)

@CordaSerializable
data class ClaimFieldReference(
        val name: String,
        val schemaId: String,
        val credDefId: String
)
@CordaSerializable
data class ClaimPredicateReference(
        val name: String,
        val p_type: String,
        val p_value: Int,
        val schemaId: String,
        val credDefId: String
)

